import { styled } from '@mui/system';
import { Box, Typography, Switch } from '@mui/material';
import { useTranslation } from 'react-i18next';
import { useState, useEffect } from 'react';

const LanguageSwitch = styled(Switch)(({ theme }) => ({
  width: 62,
  height: 34,
  padding: 7,
  '& .MuiSwitch-switchBase': {
    margin: 1,
    padding: 0,
    transform: 'translateX(6px)',
    '&.Mui-checked': {
      color: '#fff',
      transform: 'translateX(22px)',
      '& .MuiSwitch-thumb:before': {
        backgroundImage:
          "url('data:image/png;base64,')",

        backgroundSize: '100%',
        backgroundPosition: 'center center',
        backgroundRepeat: 'no-repeat',
        top: 4,
        left: 4,
        width: 24,
        height: 24,
        borderRadius: '50%',
      },
      '& + .MuiSwitch-track': {
        opacity: 1,
        backgroundColor: theme.palette.mode === 'dark' ? '#8796A5' : '#aab4be',
      },
    },
  },

  '& .MuiSwitch-thumb': {
    backgroundColor: theme.palette.mode === 'dark' ? '#003892' : '#001e3c',
    width: 32,
    height: 32,
    '&:before': {
      content: "''",
      position: 'absolute',
      backgroundImage:
        "url('data:image/png;base64,')",

      backgroundSize: '100%',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      width: 24,
      height: 24,
      top: 4,
      left: 4,
      borderRadius: '50%',
    },
  },
  '& .MuiSwitch-track': {
    opacity: 1,
    backgroundColor: theme.palette.mode === 'dark' ? '#8796A5' : '#aab4be',
    borderRadius: 20 / 2,
  },
}));

const CompLanguageSwitch = () => {
  const { i18n } = useTranslation();
  const [selectedLanguage, setSelectedLanguage] = useState('no');

  useEffect(() => {
    const storedLanguage = localStorage.getItem('language');
    setSelectedLanguage(storedLanguage || 'no');
  }, []);

  const changeLanguage = () => {
    const newLanguage = selectedLanguage === 'no' ? 'en' : 'no';
    localStorage.setItem('language', newLanguage);
    setSelectedLanguage(newLanguage);
    i18n.changeLanguage(newLanguage);
  };

  return (
    <Box>
      <LanguageSwitch onChange={changeLanguage} name="languageSwitch" checked={selectedLanguage === 'no'} />
    </Box>
  );
};

export default CompLanguageSwitch;
